const fs = require('fs');

// Function to generate SVG content
function generateSVG(text, textColor, shape, shapeColor) {
    // SVG content template
    const svgContent = `
        <svg width="300" height="200">
            <!-- Draw the shape -->
            <rect x="50" y="50" width="200" height="100" fill="${shapeColor}" />
            <!-- Add text -->
            <text x="150" y="150" text-anchor="middle" fill="${textColor}">${text}</text>
        </svg>
    `;

    return svgContent;
}

// Function to save SVG file
function saveSVG(svgContent, fileName) {
    fs.writeFileSync(fileName, svgContent);
}

// Export the functions
module.exports = {
    generateSVG,
    saveSVG
};
